            break;
         case QAPI_BLE_ET_LE_ADVERTISING_SET_TERMINATED_E:
            QCLI_Printf(ble_group, "etLE_Advertising_Set_Terminated with size %d.\n",(int)GAP_LE_Event_Data->Event_Data_Size);

